mapper.select:
SELECT COUNT(INDUST_ACCDNT_ID) OVER() AS TOTAL_COUNT , INDUST_ACCDNT_ID AS ACCIDENT_ID , TO_CHAR(OCCUR_DATE, 'YYYY') AS OCCUR_YEAR , GET_WORKPLACE_NM(WORKPLACE_ID) AS WORKPLACE_NAME , TO_CHAR(OCCUR_DATE, 'YY-MM-DD') AS OCCUR_DATE , GET_COMM_NM('005', ACC_TYPE_005_CD_001) AS ACC_TYPE_001 , GET_COMM_NM('005', ACC_TYPE_005_CD_002) AS ACC_TYPE_002 , GET_COMM_NM('005', ACC_TYPE_005_CD_003) AS ACC_TYPE_003 , GET_COMM_NM('005', ACC_TYPE_005_CD_004) AS ACC_TYPE_004 , GET_COMM_NM('005', ACC_TYPE_005_CD_005) AS ACC_TYPE_005 , GET_COMM_NM('005', ACC_TYPE_005_CD_006) AS ACC_TYPE_006 , GET_COMM_NM('006', ACC_LEVEL_006_CD) AS ACC_LEVEL , OCCUR_PLACE , MANAGER_NAME , OCCUR_REASON , PREVENT_CN , DEATH_TOLL , SAME_ACCIDENT_INJURY , JOB_DESEASE_TOLL FROM RFT_INDUSTRIAL_ACCIDENT WHERE COMPANY_ID = #{companyId} AND IS_DELETE = 0
AND WORKPLACE_ID = #{workplaceId}
AND ACC_TYPE_005_CD_001 = #{accTypeCd001}
AND ACC_TYPE_005_CD_002 = #{accTypeCd002}
AND ACC_TYPE_005_CD_003 = #{accTypeCd003}
AND ACC_TYPE_005_CD_004 = #{accTypeCd004}
AND ACC_TYPE_005_CD_005 = #{accTypeCd005}
AND ACC_TYPE_005_CD_006 = #{accTypeCd006}
AND ACC_LEVEL_006_CD = #{accLevelCd}
AND OCCUR_PLACE = #{occurPlace}
AND OCCUR_DATE >= #{startDate}::timestamp
AND OCCUR_DATE < #{endDate}::timestamp + interval '1 day'
AND MANAGER_NAME LIKE '%${managerName}%'
AND ACCIDENT_TYPE_012_CD = #{accidentTypeCd}
ORDER BY ACCIDENT_ID DESC

mapper.insert:
