  , OCCUR_DATE
  , ACC_TYPE_005_CD_001
  , ACC_TYPE_005_CD_002
  , ACC_TYPE_005_CD_003
  , ACC_TYPE_005_CD_004
  , ACC_TYPE_005_CD_005
  , ACC_TYPE_005_CD_006
  , ACC_LEVEL_006_CD
  , OCCUR_PLACE
  , MANAGER_NAME
  , OCCUR_REASON
