  , PERFORM_AFTER_ID
  , IS_ENABLE
  , IS_DELETE
  , INSERT_ID
  , INSERT_DATE
  , UPDATE_ID
  , UPDATE_DATE
  , DEATH_TOLL 
  , SAME_ACCIDENT_INJURY 
  , JOB_DESEASE_TOLL 
  , ACCIDENT_TYPE_012_CD
)
VALUES
