mapper.select:
SELECT COUNT(INDUST_ACCDNT_ID) OVER() AS TOTAL_COUNT , INDUST_ACCDNT_ID AS ACCIDENT_ID , TO_CHAR(OCCUR_DATE, 'YYYY') AS OCCUR_YEAR , GET_WORKPLACE_NM(WORKPLACE_ID) AS WORKPLACE_NAME , TO_CHAR(OCCUR_DATE, 'YY-MM-DD') AS OCCUR_DATE , GET_COMM_NM('005', ACC_TYPE_005_FALL) AS ACC_TYPE_001 , GET_COMM_NM('005', ACC_TYPE_005_CAUGHT) AS ACC_TYPE_002 , GET_COMM_NM('005', ACC_TYPE_005_FIRE) AS ACC_TYPE_003 , GET_COMM_NM('005', ACC_TYPE_005_ELECT) AS ACC_TYPE_004 , GET_COMM_NM('005', ACC_TYPE_005_CONFINED) AS ACC_TYPE_005 , GET_COMM_NM('005', ACC_TYPE_005_HEAVY) AS ACC_TYPE_006 , GET_COMM_NM('006', ACC_LEVEL_006_CD) AS ACC_LEVEL , OCCUR_PLACE , MANAGER_NAME , OCCUR_REASON , PREVENT_CN , DEATH_TOLL , SAME_ACCIDENT_INJURY , JOB_DESEASE_TOLL FROM RFT_INDUSTRIAL_ACCIDENT WHERE COMPANY_ID = #{companyId} AND BASELINE_ID = #{baselineId} AND IS_DELETE = 0
AND WORKPLACE_ID = #{workplaceId}
AND(
ACC_TYPE_005_FALL = #{accTypeCd001}
OR ACC_TYPE_005_CAUGHT = #{accTypeCd002}
OR ACC_TYPE_005_FIRE = #{accTypeCd003}
OR ACC_TYPE_005_ELECT = #{accTypeCd004}
OR ACC_TYPE_005_CONFINED = #{accTypeCd005}
OR ACC_TYPE_005_HEAVY = #{accTypeCd006}
)
AND ACC_LEVEL_006_CD = #{accLevelCd}
AND (ACC_TYPE_005_FALL = '001' or ACC_TYPE_005_CAUGHT = '002' or ACC_TYPE_005_FIRE = '003' or ACC_TYPE_005_ELECT = '004' or ACC_TYPE_005_CONFINED = '005' or ACC_TYPE_005_HEAVY = '006')
AND OCCUR_PLACE LIKE '%${occurPlace}%'
AND OCCUR_DATE >= #{startDate}::timestamp
AND OCCUR_DATE < #{endDate}::timestamp + interval '1 day'
AND MANAGER_NAME LIKE '%${managerName}%'
AND ACCIDENT_TYPE_012_CD = #{accidentTypeCd}
AND DEATH_TOLL > 0
AND SAME_ACCIDENT_INJURY > 0
AND JOB_DESEASE_TOLL > 0
ORDER BY ACCIDENT_ID DESC

mapper.insert:
INSERT INTO RFT_INDUSTRIAL_ACCIDENT
(
	COMPANY_ID
  , WORKPLACE_ID
  , RECV_DATE
  , RECV_USER_NAME
  , RECV_004_CD
  , RECV_TYPE_005_FALL  
  , RECV_TYPE_005_CAUGHT 
  , RECV_TYPE_005_FIRE 
  , RECV_TYPE_005_ELECT 
  , RECV_TYPE_005_CONFINED 
  , RECV_TYPE_005_HEAVY
  , ACCDNT_CN
  , ACC_TYPE_005_FALL  
  , ACC_TYPE_005_CAUGHT 
  , ACC_TYPE_005_FIRE 
  , ACC_TYPE_005_ELECT 
  , ACC_TYPE_005_CONFINED 
  , ACC_TYPE_005_HEAVY 
  , ACC_LEVEL_006_CD
  , OCCUR_PLACE
  , MANAGER_NAME
  , OCCUR_REASON
  , INIT_REPORT_ID
  , FINAL_REPORT_ID
  , PREVENT_CN
  , PERFORM_BEFORE_ID
  , PERFORM_AFTER_ID
  , IS_ENABLE
  , IS_DELETE
  , INSERT_ID
  , INSERT_DATE
  , DEATH_TOLL 
  , SAME_ACCIDENT_INJURY 
  , JOB_DESEASE_TOLL 
  , ACCIDENT_TYPE_012_CD
  , BASELINE_ID

  , OCCUR_DATE

)
VALUES
(
	#{companyId}
  , #{workplaceId}
  , #{recvDate}::timestamp
  , #{recvUserName}
  , #{recvFormCd}
  , #{recvTypeCd001}
  , #{recvTypeCd002}
  , #{recvTypeCd003}
  , #{recvTypeCd004}
  , #{recvTypeCd005}
  , #{recvTypeCd006}
  , #{accdntCn}
  , #{accTypeCd001}
  , #{accTypeCd002}
  , #{accTypeCd003}
  , #{accTypeCd004}
  , #{accTypeCd005}
  , #{accTypeCd006}
  , #{accLevelCd}
  , #{occurPlace}
  , #{managerName}
  , #{occurReason}
  , #{initReportId}
  , #{finalReportId}
  , #{preventCn}
  , #{performBeforeId}
  , #{performAfterId}
  , 1
  , 0
  , #{insertId}
  , now()
  , #{deathToll}
  , #{sameAccidentInjury}
  , #{jobDeseaseToll}
  , #{accidentTypeCd}
  , #{baselineId}

  , #{occurDate}::timestamp

)

mapper.update:
UPDATE RFT_INDUSTRIAL_ACCIDENT
   SET WORKPLACE_ID = #{workplaceId}
     , RECV_DATE = #{recvDate}::timestamp
     , RECV_USER_NAME = #{recvUserName}
     , RECV_004_CD = #{recvFormCd}
     , RECV_TYPE_005_FALL = #{recvTypeCd001}
     , RECV_TYPE_005_CAUGHT = #{recvTypeCd002}
     , RECV_TYPE_005_FIRE = #{recvTypeCd003}
     , RECV_TYPE_005_ELECT = #{recvTypeCd004}
     , RECV_TYPE_005_CONFINED = #{recvTypeCd005}
     , RECV_TYPE_005_HEAVY = #{recvTypeCd006}
     , ACCDNT_CN = #{accdntCn}
     , OCCUR_DATE = #{occurDate}::timestamp
     , ACC_TYPE_005_FALL = #{accTypeCd001}
     , ACC_TYPE_005_CAUGHT = #{accTypeCd002}
     , ACC_TYPE_005_FIRE = #{accTypeCd003}
     , ACC_TYPE_005_ELECT = #{accTypeCd004}
     , ACC_TYPE_005_CONFINED = #{accTypeCd005}
     , ACC_TYPE_005_HEAVY = #{accTypeCd006}
     , ACC_LEVEL_006_CD = #{accLevelCd}
     , OCCUR_PLACE = #{occurPlace}
     , MANAGER_NAME = #{managerName}
     , OCCUR_REASON = #{occurReason}
     , INIT_REPORT_ID = #{initReportId}
 	 , FINAL_REPORT_ID = #{finalReportId}
     , PREVENT_CN = #{preventCn}
     , PERFORM_BEFORE_ID = #{performBeforeId}
     , PERFORM_AFTER_ID = #{performAfterId}
     , UPDATE_ID = #{updateId}
     , UPDATE_DATE = now()
     , DEATH_TOLL = #{deathToll}
     , SAME_ACCIDENT_INJURY = #{sameAccidentInjury}
  	 , JOB_DESEASE_TOLL = #{jobDeseaseToll}
  	 , ACCIDENT_TYPE_012_CD = #{accidentTypeCd}
 WHERE COMPANY_ID =  #{companyId}
   AND INDUST_ACCDNT_ID = #{accidentId}
   AND BASELINE_ID = #{baselineId}

UPDATE RFT_INDUSTRIAL_ACCIDENT
   SET IS_DELETE = 1
     , UPDATE_ID = #{updateId}
     , UPDATE_DATE = now()
 WHERE COMPANY_ID =  #{companyId}
   AND INDUST_ACCDNT_ID = #{accidentId}

mapper.select:
SELECT DISTINCT OCCUR_PLACE AS occurPlace
  FROM RFT_INDUSTRIAL_ACCIDENT 
 WHERE COMPANY_ID = #{companyId}
   AND BASELINE_ID = #{baselineId}

   AND WORKPLACE_ID = #{workplaceId}
