mapper.select:
SELECT COUNT(IMPROVE_ID) OVER() AS TOTAL_COUNT , IMPROVE_ID , GET_WORKPLACE_NM(WORKPLACE_ID) AS WORKPLACE_NAME , TO_CHAR(REQ_DATE, 'YYYY-MM-DD') AS REQ_DATE , REQ_USER_NAME , IMPROVE_CN , IMPROVE_NO , FIN_DATE , GET_COMM_NM('009', PROCESS_009_CD) AS STATUS FROM RISK_FREE.RFT_IMPROVE WHERE COMPANY_ID = #{companyId}
AND WORKPLACE_ID = #{workplaceId}
AND REQ_DATE >= #{startDate}::timestamp
AND REQ_DATE < #{endDate}::timestamp + interval '1 day'
AND REQ_USER_NAME = #{reqUserName}
AND PROCESS_009_CD = #{statusCd}
ORDER BY IMPROVE_ID DESC

mapper.insert:
INSERT INTO rft_improve
(
  	COMPANY_ID
  , WORKPLACE_ID
  , IMPROVE_NO
  , IMPROVE_CN
  , REQ_DATE
  , REQ_USER_NAME
  , FIN_DATE
  , REQ_FILE_ID
  , PROCESS_009_CD
  , ACTION_CN
  , ACTION_BEFORE_ID
  , ACTION_AFTER_ID
  , IS_ENABLE
  , IS_DELETE
  , INSERT_ID
  , INSERT_DATE
  , UPDATE_ID
  , UPDATE_DATE
)
VALUES
(
	#{companyId}
  , #{workplaceId}
  , #{improveNo}
  , #{improveCn}
  , #{reqDate}::timestamp
  , #{reqUserName}
  , #{finDate}::timestamp
  , #{reqFileId}
  , #{statusCd}
  , #{actionCn}
  , #{actionBeforeId}
  , #{actionAfterId}
  , 1
  , 0
  , #{insertId}
  , now()
  , #{updateId}
  , now()
)

mapper.delete:
DELETE FROM RFT_IMPROVE
 WHERE COMPANY_ID = #{companyId}
   AND IMPROVE_ID = #{improveId}
