mapper.select:
SELECT COUNT(LAW_IMPROVE_ID) OVER() AS TOTAL_COUNT , COMPANY_ID , WORKPLACE_ID , LAW_IMPROVE_ID , GET_WORKPLACE_NM(WORKPLACE_ID) AS WORKPLACE_NAME , TO_CHAR(RECV_DATE,'YYYY') AS RECV_YEAR , TO_CHAR(RECV_DATE,'YY-MM-DD') AS RECV_DATE , GET_COMM_NM('009',PROCESS_009_CD) AS STATUS , GET_COMM_NM('008', CMMD_008_CD_001) AS CMMD_ORG_NAME_001 , GET_COMM_NM('008', CMMD_008_CD_002) AS CMMD_ORG_NAME_002 , GET_COMM_NM('008', CMMD_008_CD_003) AS CMMD_ORG_NAME_003 , GET_COMM_NM('008', CMMD_008_CD_004) AS CMMD_ORG_NAME_004 , OCCUR_PLACE , ISSUE_REASON , PREVENT_CN , TO_CHAR(ORDER_DATE,'YY-MM-DD') AS ORDER_DATE , TO_CHAR(DUE_DATE, 'YY-MM-DD') AS DUE_DATE , TO_CHAR(COMPLETE_DATE, 'YYYY-MM-DD') AS COMPLETE_DATE FROM RFT_LAW_IMPROVE WHERE COMPANY_ID = #{companyId} AND BASELINE_ID = #{baselineId} AND IS_DELETE = 0
AND WORKPLACE_ID = #{workplaceId}
AND(
CMMD_008_CD_001 = #{cmmdOrgCd001}
OR CMMD_008_CD_002 = #{cmmdOrgCd002}
OR CMMD_008_CD_003 = #{cmmdOrgCd003}
OR CMMD_008_CD_004 = #{cmmdOrgCd004}
)
AND(CMMD_008_CD_001 = '001' or CMMD_008_CD_002 = '002' or CMMD_008_CD_003 ='003' or CMMD_008_CD_004 = '004')
AND PROCESS_009_CD = #{statusCd}
AND ISSUE_REASON = #{issueReason}
AND ORDER_DATE >= #{startDate}::timestamp
AND ORDER_DATE < #{endDate}::timestamp + interval '1 day'
AND DUE_DATE = #{dueDate}::timestamp
AND IMPROVE_TYPE_011_CD = #{improveTypeCd}
ORDER BY LAW_IMPROVE_ID DESC

mapper.insert:
INSERT INTO RFT_LAW_IMPROVE
(
	COMPANY_ID
  , WORKPLACE_ID
  , RECV_DATE
  , RECV_USER_NAME
  , RECV_007_CD
  , CMMD_008_CD_001
  , CMMD_008_CD_002
  , CMMD_008_CD_003
  , CMMD_008_CD_004
  , IMPROVE_CN
  , OCCUR_PLACE
  , PROCESS_009_CD
  , ISSUE_REASON
  , PREVENT_CN
  , PERFORM_BEFORE_ID
  , PERFORM_AFTER_ID
  , IS_ENABLE
  , IS_DELETE
  , INSERT_ID
  , INSERT_DATE
  , UPDATE_ID
  , UPDATE_DATE
  , ORDER_DATE
  , DUE_DATE
  , BASELINE_ID 
  , IMPROVE_TYPE_011_CD

  , COMPLETE_DATE

)
VALUES
(
	#{companyId}
  , #{workplaceId}
  , #{recvDate}::timestamp
  , #{recvUserName}
  , #{recvCd}
  , #{cmmdOrgCd001}
  , #{cmmdOrgCd002}
  , #{cmmdOrgCd003}
  , #{cmmdOrgCd004}
  , #{improveCn}
  , #{occurPlace}
  , #{statusCd}
  , #{issueReason}
  , #{preventCn}
  , #{performBeforeId}
  , #{performAfterId}
  , 1
  , 0
  , #{insertId}
  , now()
  , #{updateId}
  , now()
  , #{orderDate}::timestamp
  , #{dueDate}::timestamp
  , #{baselineId}
  , #{improveTypeCd}

  , #{completeDate}::timestamp

)

mapper.update:
		UPDATE RFT_LAW_IMPROVE
   		   SET RECV_DATE = #{recvDate}::timestamp
   		     , RECV_USER_NAME = #{recvUserName}
   		     , RECV_007_CD = #{recvCd}
   		     , CMMD_008_CD_001 = #{cmmdOrgCd001}
   		     , CMMD_008_CD_002 = #{cmmdOrgCd002}
   		     , CMMD_008_CD_003 = #{cmmdOrgCd003}
   		     , CMMD_008_CD_004 = #{cmmdOrgCd004}
   		     , PREVENT_CN = #{preventCn}
   		     , OCCUR_PLACE = #{occurPlace}
   		     , PROCESS_009_CD = #{statusCd}
   		     , ISSUE_REASON = #{issueReason}
   		     , IMPROVE_CN = #{improveCn}
   		     , PERFORM_BEFORE_ID = #{performBeforeId}
   		     , PERFORM_AFTER_ID = #{performAfterId}
   		     , UPDATE_ID = #{updateId}
   		     , UPDATE_DATE = now()
   		     , ORDER_DATE = #{orderDate}::timestamp
   		     , DUE_DATE = #{dueDate}::timestamp
   		     , IMPROVE_TYPE_011_CD = #{improveTypeCd}

   		     	, COMPLETE_DATE = #{completeDate}::timestamp

 	     WHERE COMPANY_ID = #{companyId}
           AND LAW_IMPROVE_ID = #{lawImproveId}
           AND BASELINE_ID = #{baselineId}

		UPDATE RFT_LAW_IMPROVE
   		   SET IS_ENABLE = 0
		     , IS_DELETE = 1
		     , UPDATE_ID = #{updateId}
   		     , UPDATE_DATE = now()
   	     WHERE COMPANY_ID = #{companyId}
           AND LAW_IMPROVE_ID = #{lawImproveId}

mapper.select:
	SELECT DISTINCT ISSUE_REASON AS issueReason
      FROM RFT_LAW_IMPROVE
     WHERE COMPANY_ID = #{companyId} 
       AND BASELINE_ID = #{baselineId}

       AND WORKPLACE_ID = #{workplaceId}
