mapper.select:
SELECT COMPANY_ID , BASELINE_ID , BASELINE_NAME , TO_CHAR(BASELINE_START, 'YYYY-MM-DD') AS BASELINE_START , TO_CHAR(BASELINE_END, 'YYYY-MM-DD') AS BASELINE_END FROM RFT_BASELINE WHERE COMPANY_ID = #{companyId}
AND BASELINE_ID = #{baselineStart}
AND BASELINE_ID = #{baselineEnd}
AND IS_DELETE = 0 AND IS_CLOSE = 0 ORDER BY BASELINE_ID DESC LIMIT 1

SELECT COMPANY_ID , BASELINE_ID , BASELINE_NAME , TO_CHAR(BASELINE_START, 'YYYY-MM-DD') AS BASELINE_START , TO_CHAR(BASELINE_END, 'YYYY-MM-DD') AS BASELINE_END , LEAD(BASELINE_ID::INT,1,0) OVER(ORDER BY BASELINE_ID DESC) AS PREV_BASELINE , LAG(BASELINE_ID::INT,1,0) OVER(ORDER BY BASELINE_ID DESC) AS NEXT_BASELINE FROM RFT_BASELINE WHERE COMPANY_ID = #{companyId}
AND BASELINE_ID = #{baselineStart}
AND BASELINE_ID = #{baselineEnd}
AND IS_DELETE = 0 AND IS_CLOSE = 0 ORDER BY BASELINE_ID DESC

SELECT COUNT(NOTICE_ID) OVER() AS TOTAL_COUNT , INSERT_DATE , CASE WHEN (INSERT_DATE + '1 DAY') > CURRENT_TIMESTAMP THEN 'Y' ELSE 'N' END NEW_YN , COMPANY_ID , NOTICE_ID , TITLE , IMPORT_010_CD AS IMPROTCD , LEAD(NOTICE_ID::INT,1,0) OVER(ORDER BY NOTICE_ID DESC) AS PREV_NOTICE , LAG(NOTICE_ID::INT,1,0) OVER(ORDER BY NOTICE_ID DESC) AS NEXT_NOTICE FROM RFT_NOTICE RN WHERE COMPANY_ID = #{companyId} AND IS_DELETE = 0
AND NOTICE_ID = #{prevNotice}
AND NOTICE_ID = #{nextNotice}
LIMIT 5

SELECT SUM(CASE WHEN PROCESS_009_CD = '002' THEN 1 ELSE 0 END ) AS INSTRUCTION ,SUM(CASE WHEN PROCESS_009_CD = '003' THEN 1 ELSE 0 END ) AS PROGRESS ,SUM(CASE WHEN PROCESS_009_CD = '004' THEN 1 ELSE 0 END ) AS COMPLETE FROM RISK_FREE.RFT_IMPROVE WHERE 1=1 AND REQ_DATE BETWEEN #{baselineStart}::timestamp AND #{baselineEnd}::timestamp AND WORKPLACE_ID = #{workplaceId}
AND REQ_USER_003_CD = '003'
AND REQ_USER_003_CD != '003'
