mapper.select:
SELECT 
	LAW_BUTTON_ID 
	, LAW_NAME 
FROM RISK_FREE.RTF_DUTY_BUTTON
WHERE COMPANY_ID = #{companyId}
AND WORKPLACE_ID = #{workplaceId}

SELECT 
	COUNT(DUTY_IMPROVE_ID)				
FROM RISK_FREE.RFT_DUTY_IMPROVE
WHERE 	
	COMPANY_ID = #{companyId}
	AND WORKPLACE_ID = #{workplaceId}
	AND BASELINE_ID = #{targetBaselineId}

