mapper.insert:
INSERT INTO RFT_WORKPLACE
(
	COMPANY_ID
  , WORKPLACE_NAME
  , SCALE_001_CD
  , SECTOR_002_CD
  , REGIST_NO
  , CONTRACT_AMOUNT
  , CONTRACT_START_DATE
  , CONTRACT_END_DATE
  , CONTRACT_FILE_ID
  , IS_ENABLE
  , INSERT_ID
  , INSERT_DATE
  , LAST_UPDATE_ID
  , LAST_UPDATE_DATE
)
VALUES
(
	#{companyId}
  , #{workplaceName}
  , #{scaleCd}
  , #{sectorCd}
  , #{registNo}
  , #{contractAmount}
  , #{contractStartDate}::timestamp
  , #{contractEndDate}::timestamp
  , #{contractFileId}
  , #{statusCd}
  , #{insertId}
  , now()
  , #{updateId}
  , now()
)

	select max(workplace_id) as workplaceId from rft_workplace

