INSERT INTO RFT_USER
(
	COMPANY_ID
  , USER_NAME
  , EMAIL
  , TEL
  , WORKPLACE_ID
  , ROLE_003_CD
  , IS_ENABLE
  , INSERT_ID
  , INSERT_DATE
  , LAST_UPDATE_ID
  , LAST_UPDATE_DATE
  , LOGIN_ID
  , PWD
  , LAST_LOGIN_DATE
)
VALUES
(
	#{companyId}
  , #{managerName}
  , #{managerEmail}
  , #{managerTel}
  , #{workplaceId}
  , #{managerRoleCd}
  , 1
  , #{insertId}
  , now()
  ,	#{updateId}
  , now()
  , #{loginId}
  , #{pwd}
  , null
)

	select max(user_id) as userId from rft_user

mapper.update:
UPDATE RFT_COMPANY
   SET COMPANY_NAME = #{companyName}
     , CEO_ID = #{userId}
 WHERE COMPANY_ID = #{companyId}

UPDATE RFT_WORKPLACE
   SET WORKPLACE_NAME = #{workplaceName}
     , SCALE_001_CD = #{scaleCd}
     , SECTOR_002_CD = #{sectorCd}
     , REGIST_NO = #{registNo}
     , CONTRACT_AMOUNT = #{contractAmount}
     , CONTRACT_START_DATE = #{contractStartDate}::timestamp
     , CONTRACT_END_DATE = #{contractEndDate}::timestamp
     , CONTRACT_FILE_ID = #{contractFileId}
	 , IS_ENABLE = #{statusCd}
 WHERE WORKPLACE_ID = #{workplaceId}

UPDATE RFT_USER
   SET LOGIN_ID = #{loginId}
     , ROLE_003_CD = #{managerRoleCd}
     , USER_NAME = #{managerName}
     , TEL = #{managerTel}
     , EMAIL = #{managerEmail}
     , WORKPLACE_ID = #{workplaceId}
 WHERE USER_ID = #{userId}

UPDATE RFT_COMPANY
   SET CEO_ID = #{userId}
 WHERE COMPANY_ID = #{companyId}
