mapper.update:
UPDATE RFT_USER
   SET ROLE_003_CD = #{roleCd}
     , MOBILE = #{mobile}
     , EMAIL = #{email}
     , UPDATE_DATE = now()
     , UPDATE_ID = #{userId}
 WHERE USER_ID = #{userId}

	UPDATE RFT_USER
       SET PWD = #{changePwd}
         , UPDATE_DATE = now()
	     , UPDATE_ID = #{userId}
 	 WHERE USER_ID = #{userId}

mapper.select:
SELECT RU.USER_ID
  FROM RFT_COMPANY RC
  JOIN RFT_USER RU ON (RC.COMPANY_ID = RU.COMPANY_ID)
 WHERE RU.LOGIN_ID = #{loginId}
   AND RC.COMPANY_NAME = #{companyName}
   AND RC.REGIST_NO = #{registNo}
   AND RC.MANAGER_NAME = #{managerName}
